import { NextRequest, NextResponse } from 'next/server';

export const runtime = 'edge';

// Minimal valid image files as base64
const testFiles = {
  png: {
    name: 'PNG_Test.png',
    mime: 'image/png',
    // Real PNG test file from Wikimedia Commons
    data: ''
  },
  jpeg: {
    name: 'test.jpg',
    mime: 'image/jpeg',
    // 1x1 red JPEG
    data: ''
  },
  gif: {
    name: 'eglite.gif',
    mime: 'image/gif',
    // Real animated GIF from easygifanimator.net (truncated for size)
    data: ''
  },
  tiff: {
    name: 'autumn.tif',
    mime: 'image/tiff',
    // Real TIFF test file from people.math.sc.edu (truncated for size)
    data: 'TU0AKgAABwAAAQMAAQAAAAEAAAABAwABAAAAFAAAAAIDAAgAAAALAAAAAJADAAgAAAASAAAAAJEDAAgAAAAaAAAAAJMDAAoAAAAmAAAAAJQDAAgAAAAwAAAAAJYDAAgAAAA4AAAAAJYDAAIAAAAIAAAAAEA=='
  },
  pdf: {
    name: 'dummy.pdf',
    mime: 'application/pdf',
    // Real PDF from W3.org test files
    data: '' },
  heic: {
    name: 'shelf-christmas-decoration.heic',
    mime: 'image/heic',
    // Real HEIC test file from heic.digital (truncated for size)
    data: 'AAAAGGZ0eXBoZWljAAAAAG1pZjFoZWljABX2o21kYXQAAHKiJgGvJeD6qmKtFese+CvXu47VptM915KPcCyU93vujImoU7WiXbrt'
  }
};

export async function GET(request: NextRequest) {
  const { searchParams } = new URL(request.url);
  const fileType = searchParams.get('type');
  
  if (!fileType || !testFiles[fileType as keyof typeof testFiles]) {
    return NextResponse.json({ error: 'Invalid file type' }, { status: 400 });
  }
  
  const file = testFiles[fileType as keyof typeof testFiles];
  const buffer = Buffer.from(file.data, 'base64');
  
  return new NextResponse(buffer, {
    headers: {
      'Content-Type': file.mime,
      'Content-Disposition': `attachment; filename="${file.name}"`,
      'Content-Length': buffer.length.toString(),
    },
  });
}